import React, { useEffect, useState, useCallback } from "react";
import "./Nav.css";

function Nav() {
  const [show, setShow] = useState(false);

  const transitionNavBar = useCallback(() => {
    setShow(window.scrollY > 100);
  }, []);

  useEffect(() => {
    window.addEventListener("scroll", transitionNavBar);
    return () => window.removeEventListener("scroll", transitionNavBar);
  }, [transitionNavBar]);

  return (
    <div className={`nav ${show ? "nav_black" : ""}`}>
      <div className="nav_content">
        <img
          className="nav_logo img-fluid rounded-top"
          src="https://images.ctfassets.net/y2ske730sjqp/1aONibCke6niZhgPxuiilC/2c401b05a07288746ddf3bd3943fbc76/BrandAssets_Logos_01-Wordmark.jpg?w=940"
          alt="Netflix brand logo"
        />
        <img
          className="nav_avatar img-fluid rounded-top"
          src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSdeCcwNddU_99Au3d5_zVKV3ZmOT8QHF9k4w&s"
          alt="User profile avatar"
        />
      </div>
    </div>
  );
}

export default Nav;
