import React from "react";
import "./HomeScreen.css";
import Nav from "./Nav";
import Banner from "./Banner";
import Row from "./Row"; // Ensure Row component is imported
import requests from "./Requests"; // Ensure requests is imported correctly

const HomeScreen = () => {
  return (
    <div className="homeScreen">
      <Nav />
      <Banner />

      <div className="category__label">Netflix Originals</div>
      <Row
        title="NETFLIX ORIGINALS"
        fetchUrl={requests.fetchNetflixOriginals}
        isLargeRow
      />

      <div className="category__label">Trending Now</div>
      <Row title="Trending Now" fetchUrl={requests.fetchTrending} />

      <div className="category__label">Top Rated</div>
      <Row title="Top Rated" fetchUrl={requests.fetchTopRated} />

      <div className="category__label">Action Movies</div>
      <Row title="Action Movies" fetchUrl={requests.fetchActionMovies} />

      <div className="category__label">Comedy Movies</div>
      <Row title="Comedy Movies" fetchUrl={requests.fetchComedyMovies} />

      <div className="category__label">Horror Movies</div>
      <Row title="Horror Movies" fetchUrl={requests.fetchHorrorMovies} />

      <div className="category__label">Romance Movies</div>
      <Row title="Romance Movies" fetchUrl={requests.fetchRomanceMovies} />

      <div className="category__label">Documentaries</div>
      <Row title="Documentaries" fetchUrl={requests.fetchDocumentaries} />
    </div>
  );
};

export default HomeScreen;
